init:
function() {
    // 自动点击个人主页中查看更多按钮
    setInterval(function() {
        // 检查是否存在 "查看更多" 按钮
        var seeMoreButton = document.querySelector('.seeMore__1QtpQ');
        if (seeMoreButton && seeMoreButton.textContent.trim() === "查看更多") {
            seeMoreButton.click(); // 点击按钮
        }
    }, 1000);
}
