init:
function() {
    // 自动领取奖励功能
    function clickReceive() {
        var receiveButton = document.querySelector("div.taskAction__3nOcF.taskReceiveReward__16CiZ");
        if (receiveButton && receiveButton.textContent === "领取") {
            receiveButton.click();
        }
    }

    // 持续检查领取奖励元素是否存在
    setInterval(function() {
        clickReceive(); // 如果找到元素则执行领取
    }, 1000);
}
